import React, { useEffect, useState } from "react";
import API from "../utils/API";
import CardContainer from "../components/Wrapper/index";
import Row from "../components/Row";
import GalleryContext from "../utils/GalleryContext"

function Gallery() {
  const [galleryState, setGalleryState] = useState({
    title: "",
    image: "",
    language: "",
  });
  const [user, setUser] = useState();
  const [users, setUsers] = useState([]);
  const [userIndex, setUserIndex] = useState(0);

  // When the component mounts, a call will be made to get random users.
  useEffect(() => {
    loadUsers();
  }, []);

  function nextUser(userIndex) {
    // Ensure that the user index stays within our range of users
    if (userIndex >= users.length) {
      userIndex = 0;
    }
    setUser(users[userIndex]);
    setUserIndex(userIndex);
  }

  function previousUser(userIndex) {
    // Ensure that the user index stays within our range of users
    if (userIndex < 0) {
      userIndex = users.length - 1;
    }
    setUser(users[userIndex]);
    setUserIndex(userIndex);
  }

  function handleBtnClick(event) {
    // Get the title of the clicked button
    const btnName = event.target.getAttribute("data-value");
    if (btnName === "next") {
      const newUserIndex = userIndex + 1;
      nextUser(newUserIndex);
    } else {
      const newUserIndex = userIndex - 1;
      previousUser(newUserIndex);
    }
  }

  function loadUsers() {
    API.getLanguagesList()
      .then(languages => {
        API.getUsersByLanguage(languages[0]).then(users => {
          setUsers(users);
          console.log(users);
          setUser(users[0]);
          setGalleryState({
            title: users[0].login,
            image: users[0].image,
            language: users[0].language
          });
        });
      })
      .catch(err => console.log(err));
  }

  return (
    <GalleryContext.Provider value={{user, users, handleBtnClick }}>
    <div>
      <h1 className="text-center">Welcome to LinkedUp</h1>
      <h3 className="text-center">Click on the arrows to browse users</h3>
      <Row>
        <CardContainer

        />
      </Row>
    </div>
    </GalleryContext.Provider>
  );
}

export default Gallery;
